mapper.select:


AND btt.bid_end_date > now()



    AND btt.cid = #{cid}


    AND (btt.bid_item_name LIKE CONCAT('%', #{searchQuery}, '%')
    OR btt.bid_item_desc LIKE CONCAT('%', #{searchQuery}, '%'))


AND btt.item_Status = #{auctionStatus}



ORDER BY btt.bid_end_date
LIMIT #{pageable.offset}, #{pageable.pageSize}

SELECT COUNT(*) FROM bid_items_tbl btt JOIN bid_item_img bii ON btt.bid_item_id = bii.bid_item_id WHERE bii.bid_repimg_yn = 'Y'
AND btt.cid = #{cid}
AND (btt.bid_item_name LIKE CONCAT('%', #{searchQuery}, '%') OR btt.bid_item_desc LIKE CONCAT('%', #{searchQuery}, '%'))
